func Part1() int {
	floor := 0
	for _, char := range INPUT {
		switch char {
		case '(':
			floor++
		case ')':
			floor--
		default:
			panic("Invalid character in input")
		}
	}
	return floor
}

func Part2() int {
	floor := 0
	for pos, char := range INPUT {
		switch char {
		case '(':
			floor++
		case ')':
			floor--
		default:
			panic("Invalid character in input")
		}

		if floor == -1 {
			return pos + 1
		}
	}
	panic("Never entered basement aparently?")
}
